import React,{useState,useEffect} from 'react'
import './UserListItem.css'
import { db } from '../../firebase'
import Popup from 'reactjs-popup';

const umailExtractor = (umail)=>{
    return umail.slice(0,umail.lastIndexOf('@'))
}


function UserItem({uid}) {
    const [dp,setDp]=useState('data:image/png;base64,')
    const [uname,setUname]=useState('Loading...')
    const [umail,setUmail] = useState('')
    const [status,setStatus]=useState('')

    useEffect(()=>{
      db.collection('users').doc(uid).get() 
      .then((user)=>{
          setUname(user.data().uname)
          setDp(user.data().dp)
          setUmail(user.data().umail)
          setStatus(user.data().status)
      })
    },[])


    return (
   
                <div  className="chat__list__item" >
                      <Popup trigger={<img  className="chat__dp dp__zoom" src={dp}/>} modal>
                        {close =>( <img  className="full__img" src={dp} onClick={close}/>)}
                     </Popup>
                   
                   
                    <div className="chat__details">
                        <h2 style={{display:'inline-flex',marginRight:'5px'}} className="theme__h4">{uname}  </h2>
                        <h4 style={{display:'inline-flex'}} className="theme__h5 theme__subfont chat__subtext">
                            {umail ? `@${umailExtractor(umail)}` : 'Loading ...'}
                        </h4> 
                        <h4 className="theme__h5 theme__subfont chat__subtext">
                            {status}
                        </h4>
                    </div>
                    
    

                </div>

    )
}

export default UserItem
